mapper.select:
SELECT
    s.store_id AS store_id, s.store_name AS store_name, s.store_menu AS store_menu, s.store_status AS store_status, s.category_bakery AS category_bakery,s.category_fruit AS category_fruit, s.category_salad AS category_salad, s.category_others  AS category_others, s.closed_at AS closed_at,
    pf.product_no AS product_no, pf.pickup_start AS pickup_start,pf.pickup_end AS pickup_end, pf.origin_price AS origin_price, pf.sale_price AS sale_price, pf.product_explain AS product_explain, pf.reservation_end AS reservation_end, pf.amount AS amount,
    img.first_img AS store_img
FROM Store s
    LEFT JOIN Product pf
    ON pf.store_id = s.store_id
    AND DATE(pf.reservation_end) = CURDATE()
LEFT JOIN (
    SELECT
        store_id,
        MIN(store_img) AS first_img
    FROM Image
    GROUP BY store_id
) img ON img.store_id = s.store_id


        AND s.store_status = #{filter.store_status} AND pf.amount >= ${filter.amount}


        AND DATE(pf.pickup_start) = #{filter.pickup_start}


        AND s.category_bakery = #{filter.category_bakery}


        AND s.category_fruit = #{filter.category_fruit}


        AND s.category_salad = #{filter.category_salad}


        AND s.category_others = #{filter.category_others}



        AND (
            s.store_name LIKE CONCAT('%', #{filter.search}, '%')
            OR s.store_menu LIKE CONCAT('%', #{filter.search}, '%')
            OR pf.product_explain LIKE CONCAT('%', #{filter.search}, '%')
        )


ORDER BY (s.store_status = 'N') ASC;

SELECT s.store_id AS store_id, s.store_name AS store_name, s.store_menu AS store_menu, s.store_status AS store_status, s.category_bakery AS category_bakery,s.category_fruit AS category_fruit, s.category_salad AS category_salad, s.category_others AS category_others, s.closed_at AS closed_at, pf.product_no AS product_no, pf.pickup_start AS pickup_start,pf.pickup_end AS pickup_end, pf.origin_price AS origin_price, pf.sale_price AS sale_price, pf.product_explain AS product_explain, pf.reservation_end AS reservation_end, pf.amount AS amount, img.first_img AS store_img FROM Bookmark b JOIN Store s ON s.store_id = b.store_id LEFT JOIN Product pf ON pf.store_id = s.store_id AND DATE(pf.reservation_end) = CURDATE() LEFT JOIN ( SELECT store_id, MIN(store_img) AS first_img FROM Image GROUP BY store_id ) img ON img.store_id = s.store_id
b.user_id = #{user_id}
ORDER BY (s.store_status = 'N') ASC;

mapper.delete:
DELETE FROM Bookmark
WHERE store_id = #{store_id}
AND user_id = #{user_id};

mapper.select:
SELECT pa.paid_no AS paid_no, pa.paid_price AS paid_price, pa.paid_status AS paid_status, s.store_name AS store_name, s.store_id AS store_id, p.product_no AS product_no, p.pickup_start AS pickup_start, p.pickup_end AS pickup_end, p.sale_price AS sale_price, p.reservation_end AS reservation_end o.order_no AS order_no, o.ordered_at AS ordered_at, o.quantity AS quantity, o.order_status AS order_status, o.canceld_from AS canceld_from, o.order_code AS order_code, o.user_id AS user_id, MIN(i.store_img) AS store_img FROM Store s JOIN Product p ON s.store_id = p.store_id JOIN Image i ON i.store_id = s.store_id JOIN Orders o ON o.product_no = p.product_no JOIN Paid pa ON pa.order_no = o.order_no
AND o.user_id = #{filter.user_id}
AND o.order_status IN
#{status}
AND o.order_status = #{filter.order_status}
GROUP BY pa.paid_no, pa.paid_price, pa.paid_status, s.store_name, s.store_id, p.product_no, p.pickup_start, p.pickup_end, p.sale_price, o.order_no, o.ordered_at, o.quantity, o.order_status, o.canceld_from, o.order_code, o.user_id;

mapper.update:
UPDATE Orders


        order_status = #{order_request.order_status},


        canceld_from = #{order_request.canceld_from},


        order_code = #{order_request.order_code},


WHERE order_no = #{order_request.order_no}
AND user_id = #{order_request.user_id};

UPDATE Alarm SET alarm_read = 'Y'
WHERE alarm_read = 'N'
AND receive_id = #{user_id};

UPDATE Alarm SET alarm_displayed = 'N'
WHERE alarm_no = #{alarm_no}
AND receive_id = #{user_id};

mapper.select:
	SELECT COUNT(*)  
	FROM Account
 	WHERE user_id = #{user_id}
